//383. 赎金信class Solution {public:    bool canConstruct(string ransomNote, string magazine) {        //unordered_map<char, int> m1;        //unordered_map<char, int> m2;        int i = 0, j = 0;        int n = ransomNote.size(), m = magazine.size();        int all[26] = {0};        for(; j < m; j++){            all[magazine[j] - 'a']++;        }        for(; i < n; i++){            all[ransomNote[i] - 'a']--;            if(all[ransomNote[i] - 'a'] < 0) return false;        }        return true;    }};